INSERT INTO master-recordings(ID,title,slug,status,lat,lng,region,location,recording_date,publish_date,recordist,charity,habitat,mood,predominant_sound,notes,audio_id,duration,is_livestream,streaming_url,thumbnail_id) VALUES (21399,'Cano Negro','cano-negro','publish','10.736037','-84.789585','Costa Rica','Cano Negro Wildlife Sanctuary',NULL,'2022-04-12',23873,23840,380,364,374,'<p>Recorded whilst on a canoe down the river Cano Negro in Costa Rica. As we passed through a mangrove forest, the calls of Howler Monkeys echoed from the water surface. On the other side of the river, Spider Monkeys climbed through the trees and a Montezuma&#8217;s Oropendola sang. Just after 4:00 a caiman jumps into the water.</p>',21043,336,NULL,NULL,21920);
